import React, { Component } from 'react';
import Fade from 'react-reveal/Fade';
import data from '../yourdata';
import { Button, UncontrolledPopover, PopoverHeader, PopoverBody } from 'reactstrap';

class About extends Component {
    state = {  }
    render() { 
        return (<div className='about'>
            <div className='about-content'>
            <h1><Fade bottom cascade>About.</Fade></h1>
            <Fade bottom>
                    <p>{data.abouttext}</p>
                    <UncontrolledPopover trigger="legacy" placement="bottom" target="PopoverLegacy">
                        <PopoverHeader>Legacy Trigger</PopoverHeader>
                        <PopoverBody>
                        I have done many things in life starting from a young age. I was in one of the first public eStem academy in Ohio, then later on getting my bachelor at The Ohio State University as a first generation student. Through out my college experience, I aquired the skills of bartending, the knowledge and title of professional bodybuilding, and the importance of user experience design. I then transitioned my knowledge and skills out into the oilfields of North Dakota, where my position consisted of working with pipeline automation technolgy.
                        </PopoverBody>
                    </UncontrolledPopover>
                    <Button id="PopoverLegacy" type="button">
                         Continue to read more of my Life Journey
                    </Button>

            </Fade>
            </div>
            {data.ShowAboutImage ? <img src={data.aboutImage} alt='about iamge' height='500' width='500'></img> : null}


        </div>  );
    }
}

export default About;
